import { useEffect, useState } from "react";
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { getUserData } from "./api";

const Main = () => {
  const [myName, setMyName] = useState(null);
  const [myFolderName, setMyForderName] = useState(null);
  const [myProfileImg, setMyProfileImg] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const whoAmI = await getUserData(`/api/sample/folder`);
        setMyName(whoAmI.owner.name);
        setMyForderName(whoAmI.name);
        setMyProfileImg(whoAmI.owner.profileImageSource);
      } catch (error) {
        console.error(error);
      }
    };

    fetchUser();
  }, []);

  return (
    <>
      <div className="topContainer">
        <img
          className="topContainer--myProfile"
          src={myProfileImg}
          alt="myProfileImg"
        />
        <p>`${myName}`</p>
        <h2>{myFolderName}</h2>
      </div>
      <div className="mainContainer">
        <div className="mainContainer--search">
          <input placeholder="링크를 검색해 보세요.">
            {/* <FontAwesomeIcon icon="fa-solid fa-magnifying-glass" /> */}
          </input>
        </div>
        {/*카드 컴포넌트 map으로 배열*/}
      </div>
    </>
  );
};

export default Main;
