import { useState } from "react";
import LogoImg from "../img/logo.svg";
import "../landing.css";
import Profile from "./Profile";
import { getEmail } from "./api";

const Header = () => {
  const [user, setUser] = useState(null); //프로필 정보 없으면 로그인 버튼 나오게 state 값 변경

  const LoadUserProfile = async () => {
    try {
      const userEmail = await getEmail().email;
      setUser(userEmail);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <header className="headerWrap">
      <nav className="headerWrap__navWrap">
        <a className="headerWrap__logo" href="./">
          <img
            className="headerWrap__img"
            src={LogoImg}
            alt="LinkbraryLogoImg"
          />
        </a>
        {user ? (
          <Profile user={user}>{user}</Profile>
        ) : (
          <a className="headerWrap__Btn" href="./signin.html">
            로그인
          </a>
        )}
      </nav>
    </header>
  );
};

export default Header;
