import { useEffect, useState } from "react";
import { getFetchData } from "../utils/getFetchData";
import Card from "./Card";
import LinkIcon from "../img/LinkIcon.svg";

// {
//   id: null,
//   created_at: null,
//   updated_at: null,
//   url: null,
//   title: null,
//   description: null,
//   image_source: null,
//   folder_id: null,
// }

const MainContainer = () => {
  const [folders, setFolders] = useState([]);

  useEffect(() => {
    const getUserFolderData = async () => {
      try {
        const result = await getFetchData(`/api/users/1/links`);
        setFolders(result.data);
      } catch (error) {
        console.error(error);
      }
    };

    getUserFolderData();
  }, []);

  return (
    <>
      <div>
        <input placeholder="링크를 추가해 보세요" />
        <img src={LinkIcon} alt="LinkIcon" />
        <button>추가하기</button>
      </div>

      <div className="container">
        <input
          className="container__search--input"
          placeholder="링크를 검색해 보세요."
        ></input>
        <div className="container__contentWrap">
          {folders.map((folder) => {
            return <Card folder={folder} />;
          })}
        </div>
      </div>
    </>
  );
};

export default MainContainer;
