import { useEffect, useState } from "react";
import { getData } from "../../utils/getData";
import "../../landing.css";
import CardWrap from "./CardWrap";
import MainProfileWrap from "./MainProfileWrap";

const Main = () => {
  const [userData, setUserData] = useState({
    myName: null,
    myFolderName: null,
    myProfileImg: null,
    folders: [],
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const data = await getData(`/api/sample/folder`);
        const whoAmI = data.folder;
        setUserData({
          myName: whoAmI.owner.name,
          myFolderName: whoAmI.name,
          myProfileImg: whoAmI.owner.profileImageSourse,
          folders: whoAmI.links,
        });
      } catch (error) {
        console.error(error);
      }
    };

    fetchUser();
  }, []);

  return (
    <>
      <MainProfileWrap userData={userData} />
      <CardWrap folders={folders} />
    </>
  );
};

export default Main;
