import Footer from "components/FolderPage/Footer/Footer";
import Header from "components/FolderPage/Header/Header";
import Main from "components/FolderPage/Main/Main";
import Nav from "components/FolderPage/Nav/Nav";
import useAsync from "hook/useAsync";
import GetUserFolder from "api/GetUserFolder";
import { useState, useEffect } from "react";

function Folder() {
  const [search, setSerch] = useState("");
  const [folderListInfo, setFolderListInfo] = useState({});
  const [isLoading, loadingError, AsyncedFolderListInfo] =
    useAsync(GetUserFolder);

  const handleSearchChange = (e) => {
    setSerch(e.target.value);
  };

  const loadFolderListInfo = async () => {
    try {
      const { data } = await AsyncedFolderListInfo();
      setFolderListInfo(data[0]);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    loadFolderListInfo();
  }, []);

  return (
    <>
      <Nav />
      <Header />
      <Main
        search={search}
        folderListInfo={folderListInfo}
        onChange={handleSearchChange}
        loadingError={loadingError}
      />
      <Footer />
    </>
  );
}

export default Folder;
