import { useEffect, useState } from "react";
import { getFetchData } from "../utils/getFetchData";
import CardWrap from "./CardWrap";
import FolderTopContainer from "./FolderTopContainer";
import LinkIcon from "../img/LinkIcon.svg";

const MainContainer = () => {
  const [userData, setUserData] = useState({
    id: null,
    created_at: null,
    favorite: null,
  });

  useEffect(() => {
    const fetchFolder = async () => {
      try {
        const getResult = await getFetchData(`/api/users/1/links`);
        setUserData(getResult.data);
        console.log(getResult);
      } catch (error) {
        console.error(error);
      }
    };

    fetchFolder();
  }, []);

  return (
    <>
      <div>
        <input placeholder="링크를 추가해 보세요" />
        <img src={LinkIcon} alt="LinkIcon" />
        <button>추가하기</button>
      </div>
      {/* <CardWrap folders={userData} /> */}
    </>
  );
};

export default MainContainer;
