function CardListItem({ link }) {
  return (
    <a href={link?.url} target="_blank" className="card">
      <div className="card-container">
        <div className="card-image">
          <img src={link?.imageSource || noImg} alt="카드 커버이미지" />
        </div>
        <div className="card-description">
          <p className="updateTime">{getTimeAgo(link.createdAt)}</p>
          <h2 className={"link-title"}>{link.title}</h2>
          <h2 className="link-description">{link.description}</h2>
          <p className={style["link-date"]}>{formatDate(link.createdAt)}</p>
        </div>
      </div>
    </a>
  );
}

export default CardListItem;
