import { useEffect, useState } from "react";
import getData from "../../getData";
import "../../landing.css";
import CardWrap from "./CardWrap";
import MainProfileWrap from "./MainProfileWrap";
const Main = () => {
  const [myName, setMyName] = useState(null);
  const [myFolderName, setMyForderName] = useState(null);
  const [myProfileImg, setMyProfileImg] = useState(null);
  const [folders, setFolders] = useState([]);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const whoAmI = await getUserData(`/api/sample/folder`).folder;
        setMyName(whoAmI.owner.name);
        setMyForderName(whoAmI.name);
        setMyProfileImg(whoAmI.owner.profileImageSource);
        setFolders(whoAmI.links);
        console.log(whoAmI.links);
      } catch (error) {
        console.error(error);
      }
    };

    fetchUser();
  }, []);

  return (
    <>
      <MainProfileWrap
        myName={myName}
        myFolderName={myFolderName}
        myProfileImg={myProfileImg}
      />
      <CardWrap folders={folders} />
    </>
  );
};

export default Main;
