import { getElapsedTime } from "./getElapsedTime";

function Content({ folder }) {
  console.log(new Date(folder.createAt));

  return (
    <div className="container__content">
      <div className="container__content--imgWrap">
        <img
          className="container__content--img"
          src={folder.imageSource}
          alt="contentImg"
        />
      </div>
      <div className="container__content--text">
        <p className="container__content--uploadTime">
          {getElapsedTime(folder.createAt)}
        </p>
        <p className="container__content--description">{folder.description}</p>
        <p className="container__content--createAt">{folder.createAt}</p>
      </div>
    </div>
  );
}

function Container({ folders }) {
  return (
    <div className="container">
      {folders.map((folder) => {
        return <Content id={folder.id} folder={folder} />;
      })}
    </div>
  );
}

export default Container;
